INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27402, 'Cabazitaxel', 'Apalutamide', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme. However, no formal drug interaction studies have been conducted.', 'DDInter', 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown. Caution is advised if they are used with cabazitaxel.', 'Metabolism', 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27455/', '', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27403, 'Cabozantinib', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible. If chronic coadministration is required, the daily dosage of cabozantinib should be increased in accordance with the prescribing information for the specific product. For the treatment of progressive, metastatic medullary thyroid cancer (capsule formulation), the recommendation is to increase cabozantinib dosage by 40 mg (e.g., from 140 mg to 180 mg daily or from 100 mg to 140 mg daily) as tolerated, not to exceed 180 mg/day.', 'Metabolism', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27456/', '', 'Alectinib, Binimetinib, Dacomitinib', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Exemestane', 1767369485);
