INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27407, 'Cariprazine', 'Apalutamide', 'Moderate', 'Coadministration with potent or moderate inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', 'DDInter', 'Concomitant use of cariprazine with potent or moderate CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27460/', '', 'Loxapine, Molindone, Thiothixene', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Exemestane', 1767369485);
