INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27694, 'Posaconazole', 'Apalutamide', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27747/', '', 'Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27695, 'Pralsetinib', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A may significantly decrease the plasma concentrations of pralsetinib which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided. If coadministration with potent CYP450 3A inducers is necessary, the initial dose of pralsetinib should be doubled starting on Day 7 of coadministration with the strong CYP450 3A4 inducer. Once the potent CYP450 3A inducer has been discontinued for at least 14 days, the pralsetinib dose taken prior to initiating the potent CYP450 3A4 inducer may be resumed.', 'Metabolism', 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27748/', '', 'Anastrozole, Aminoglutethimide, Fulvestrant', 'Alectinib, Binimetinib, Dacomitinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (27696, 'Pravastatin', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27749/', '', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Niacin, Lomitapide, Fenofibrate, More', 'Fulvestrant, Toremifene, Abarelix, Flutamide, Enzalutamide, Relugolix, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone', 1767369485);
