INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28318, 'Bupropion', 'Methylphenobarbital', 'Moderate', 'Excessive use or abrupt discontinuation of barbiturates and other sedatives after chronic ingestion may precipitate seizures in patients receiving bupropion. Theoretically, barbiturates may also decrease the plasma concentrations and pharmacologic effects of bupropion due to induction of hepatic CYP450 isoenzymes.', 'DDInter', 'Pharmacologic response to bupropion should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary. Although barbiturates and other sedatives may be prescribed to treat agitation, anxiety, and insomnia associated with bupropion use, patients should be alerted to the possibility of an increased risk of seizures during excessive exposure to these drugs. Patients should not attempt to alter the dosages or discontinue the medications on their own without consulting with their physician. The use of bupropion is contraindicated in patients undergoing abrupt discontinuation of sedatives.', 'Metabolism', 'Pharmacologic response to bupropion should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28371/', '', 'Ethotoin, Brivaracetam, Methsuximide, Ezogabine, Paramethadione, Perampanel, Eslicarbazepine, Phensuximide, Ganaxolone, Phenacemide, Felbamate, More', 'Dexfenfluramine, Naltrexone, Ephedrine, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol, Oxitriptan, Viloxazine, Tryptophan', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28319, 'Bupropion', 'Meprobamate', 'Moderate', 'Excessive use or abrupt discontinuation of benzodiazepines and other sedatives after chronic ingestion may precipitate seizures in patients receiving bupropion. Conversely, bupropion may antagonize the central pharmacologic effects of sedatives. Bupropion can cause agitation, anxiety, and insomnia and has been shown to decrease the sedative effect of diazepam in healthy volunteers given single doses of the drugs.', 'DDInter', 'Although sedatives may be prescribed to treat agitation, anxiety, and insomnia associated with bupropion use, patients should be alerted to the possibility of an increased risk of seizures during excessive exposure to these drugs. Patients should not attempt to alter the dosages or discontinue the medications on their own without consulting with their physician. The use of bupropion is contraindicated in patients undergoing abrupt discontinuation of sedatives.', 'Antagonism', 'Although sedatives may be prescribed to treat agitation, anxiety, and insomnia associated with bupropion use, patients should be alerted to the possibility of an increased risk of seizures during excessive exposure to these drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28372/', '', 'Suvorexant, Thiopental, Ramelteon, Scopolamine, Dexmedetomidine, Methohexital, Tasimelteon, Melatonin, Dichloralphenazone, Valerian', 'Dexfenfluramine, Naltrexone, Ephedrine, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol, Oxitriptan, Viloxazine, Vortioxetine, More', 1767369485);
