INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28362, 'Bupropion', 'Norfloxacin', 'Major', 'The use of bupropion is associated with a dose-related risk of seizures. The risk may be further increased when coadministered with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline).', 'DDInter', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Bupropion as well as concomitant medications should be initiated at the lower end of the dosage range and titrated gradually as needed and as tolerated. The maximum recommended dosage for the specific bupropion formulation should not be exceeded. Bupropion should be discontinued and not restarted in patients who experience a seizure during treatment.', 'Synergism', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28415/', '', 'Tinidazole, Cefepime, Secnidazole, Cefixime, Amikacin, Rifamycin, Kanamycin, Oxytetracycline, Netilmicin, Polymyxin B, Sulfamethizole, More', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Sibutramine, Diethylpropion, Mazindol, Phenelzine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28363, 'Bupropion', 'Nortriptyline', 'Major', 'The use of bupropion is associated with a dose-related risk of seizures. The risk may be further increased when coadministered with other agents that can reduce the seizure threshold, including antidepressants, CNS stimulants, acetylcholinesterase inhibitors, phenothiazines, and dopaminergic blocking agents such as neuroleptics and metoclopramide. Coadministration with bupropion may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many antidepressants, neuroleptics, CNS stimulants (e.g., amphetamines), metoclopramide, and some acetylcholinesterase inhibitors (e.g., donepezil, galantamine). The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by bupropion and its metabolite, hydroxybupropion.', 'DDInter', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Bupropion as well as concomitant medications should be initiated at the lower end of the dosage range and titrated gradually as needed and as tolerated. The maximum recommended dosage for the specific bupropion formulation should not be exceeded.', 'Metabolism', 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28416/', '', 'St. John''s Wort, Oxitriptan, Viloxazine, Tryptophan, Bupropion, Esketamine', 'Naltrexone, Nortriptyline', 1767369485);
