INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28502, 'Arsenic trioxide', 'Apomorphine', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy. Caution is advised if concomitant use cannot be avoided. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. An absolute QT interval exceeding 500 msec will require immediate action to correct concomitant risk factors, if any, as well as a thorough assessment of the need for continued therapy. Patients who develop syncope or arrhythmia should be hospitalized for clinical and laboratory monitoring.', 'Synergism', 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28555/', '', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, Avanafil, More', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
