INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28871, 'Solriamfetol', 'Apomorphine', 'Moderate', 'Coadministration of solriamfetol with other drugs that increase dopamine levels or that bind directly to dopamine receptors may increase the risk of dopaminergic-related adverse effects. The mechanism of the interaction has not been reported, but it may involve synergistic or additive central dopaminergic effects.', 'DDInter', 'Caution is advised if solriamfetol is administered concomitantly with other dopaminergic drugs. Dosage adjustments and close patient monitoring should be considered whenever solriamfetol is used with other dopaminergic drugs.', 'Synergism', 'Caution is advised if solriamfetol is administered concomitantly with other dopaminergic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28924/', '', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, More', 'Amphetamine, Pemoline, Metamfetamine, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Armodafinil, Citicoline, Modafinil', 1767369485);
