INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (29394, 'Dicoumarol', 'Aprepitant', 'Moderate', 'Coadministration of aprepitant or its prodrug, fosaprepitant, during warfarin therapy may result in clinically significant decreases in the INR or prothrombin time. The likely mechanism is aprepitant induction of CYP450 2C9, the isoenzyme responsible for metabolic clearance of the biologically more active S(-) enantiomer of warfarin.', 'DDInter', 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking.', 'Metabolism', 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29447/', '', 'Betrixaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Nabilone, Granisetron, Scopolamine, Dronabinol, Rolapitant, Dolasetron, Ondansetron, Palonosetron', 1767369485);
