INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (29625, 'Tolvaptan', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tolvaptan, which is almost exclusively metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of tolvaptan with moderate CYP450 3A4 inhibitors should generally be avoided unless no alternatives exist and benefits outweigh potential risks. A lower dosage of tolvaptan should be considered. Serum sodium concentration and neurologic status should be closely monitored during coadministration. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Metabolism', 'Concomitant use of tolvaptan with moderate CYP450 3A4 inhibitors should generally be avoided unless no alternatives exist and benefits outweigh potential risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29678/', '', 'Nabilone, Granisetron, Scopolamine, Dronabinol, Dolasetron, Ondansetron, Palonosetron', '', 1767369485);
