INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (29719, 'Desogestrel', 'Pegaspargase', 'Moderate', 'Theoretically, there is potential for an indirect interaction between asparaginase and oral contraceptives. The mechanism may involve asparaginase''s hepatotoxicity, which may impair oral contraceptives'' hepatic clearance.', 'DDInter', 'The use of oral contraceptives during asparaginase treatment is not recommended. Other non-oral contraceptive methods should be used in women of childbearing potential. According to the manufacturer, females of reproductive potential should be advised to use effective contraception during asparaginase treatment and for at least 3 to 6 months after the last dose.', 'Synergism', 'The use of oral contraceptives during asparaginase treatment is not recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29772/', '', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Porfimer sodium, Niraparib, More', 'Ulipristal, Norelgestromin, Megestrol acetate, Estradiol, Estetrol, Etonogestrel, Megestrol acetate, Progesterone, Estradiol', 1767369485);
