INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30400, 'Isoetharine', 'Aripiprazole', 'Moderate', 'It is uncertain whether aripiprazole causes clinically significant prolongation of the QT interval.', 'DDInter', 'Some authorities recommend caution when aripiprazole is used with drugs that are known to cause QT prolongation. ECG monitoring may be advisable in some cases, such as in patients with a history of cardiac arrhythmias or congenital or family history of long QT syndrome. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Others', 'Some authorities recommend caution when aripiprazole is used with drugs that are known to cause QT prolongation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30453/', '', 'Methotrimeprazine, Promazine, Thiothixene, Lumateperone, Triflupromazine, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', '', 1767369485);
