INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30462, 'Methylphenobarbital', 'Aripiprazole', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aripiprazole, which is partially metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary. The manufacturer recommends doubling the aripiprazole dosage when a potent CYP450 3A4 inducer is added to therapy, and additional dosage adjustments made based on clinical evaluation. Potent inducers include carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with aripiprazole is unknown. The aripiprazole dosage should be reduced following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Pharmacologic response to aripiprazole should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the aripiprazole dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30515/', '', 'Lacosamide, Rufinamide', 'Amisulpride', 1767369485);
