INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30878, 'Glycerin', 'Triamcinolone', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', 'DDInter', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30931/', '', 'Mometasone furoate, Amcinonide, Budesonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, More', 'Alvimopan, Sodium sulfate, Sorbitol, Methylnaltrexone, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, Naldemedine, Prucalopride, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30879, 'Glycerol phenylbutyrate', 'Triamcinolone', 'Moderate', 'Drugs that can increase plasma ammonia levels such as corticosteroids, haloperidol, or the anticonvulsants carbamazepine, topiramate, and phenobarbital may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Corticosteroids can cause the breakdown of body protein, which may lead to increased ammonia levels. The use of haloperidol has been associated with hyperammonemia in a young child with citrullinemia, an inherited disorder of ammonia excretion.', 'DDInter', 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/30932/', '', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Agalsidase beta, Givosiran, Lipoic acid, More', 1767369485);
