INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30965, 'Triamcinolone', 'Methazolamide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31018/', '', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Ciclesonide, Mupirocin, Mometasone furoate, More', 'Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Mitapivat, Vemurafenib, Probenecid, Tioconazole, Furazolidone, Diiodohydroxyquinoline, Oxytetracycline, More', 1767369485);
