INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30980, 'Triamcinolone', 'Moxifloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', 'DDInter', 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31033/', '', 'Ganciclovir, Rifamycin, Kanamycin, Oxytetracycline, Netilmicin, Polymyxin B, Sulfamethizole, Trifluridine, Tobramycin, Ampicillin, Benzylpenicillin, More', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mometasone furoate, Mometasone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (30981, 'Triamcinolone', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31034/', '', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', '', 1767369485);
