INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31081, 'Triamcinolone', 'Stanozolol', 'Moderate', 'Concomitant use of androgens with adrenocorticotropic hormone (ACTH) or corticosteroids may result in increased fluid retention and edema due to additive steroidal effects.', 'DDInter', 'Caution is advised if androgens are used in combination with ACTH or corticosteroids, particularly in patients with conditions that may be aggravated by fluid retention such as cardiovascular, renal, or hepatic diseases.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31134/', '', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', 'Prasterone', 1767369485);
