INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31398, 'Ramelteon', 'Armodafinil', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations and pharmacologic effects of ramelteon, which is metabolized by CYP450 1A2 and, to a lesser extent, by CYP450 3A4 and the 2C subfamily of isoenzymes.', 'DDInter', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St. John''s wort. Other known inducers include aminoglutethimide, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, mitotane, modafinil, nafcillin, nevirapine, rifabutin, rifapentine, barbiturates and various other anticonvulsants, although the extent to which they interact with ramelteon is unknown.', 'Metabolism', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31451/', '', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Solriamfetol, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31399, 'Ranolazine', 'Armodafinil', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The extent to which other, less potent CYP450 3A4 inducers may interact with ranolazine is unknown. Caution is advised if they are used with ranolazine.', 'Metabolism', 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31452/', '', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Adenosine, Regadenoson', 'Pemoline, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', 1767369485);
