INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31650, 'Polyethylene glycol (3350 with electrolytes)', 'Arsenic trioxide', 'Major', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly. Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31703/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31651, 'Polythiazide', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31704/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Darunavir, Probenecid, Tioconazole, More', 1767369485);
