INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (31701, 'Trichlormethiazide', 'Arsenic trioxide', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of arsenic trioxide with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with arsenic trioxide. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Caution is advised if arsenic trioxide must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31754/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Darunavir, Probenecid, Tioconazole, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
