INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33095, 'Bexarotene', 'Asparaginase Escherichia coli', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', 'DDInter', 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33148/', '', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Altretamine, Tagraxofusp, Enasidenib, Selinexor, Aflibercept, Trastuzumab, Anagrelide, Hydroxyurea, More', 'Pertuzumab, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Belzutifan, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33096, 'Bicalutamide', 'Asparaginase Escherichia coli', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', 'DDInter', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33149/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 'Darolutamide, Anastrozole, Aminoglutethimide, Letrozole, Toremifene, Fulvestrant, Abarelix, Enzalutamide, Relugolix, Degarelix, Apalutamide, More', 1767369485);
