INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33788, 'Acetylsalicylic acid', 'Valproic acid', 'Moderate', 'Salicylates, particularly aspirin, may displace valproate from protein binding sites and inhibit its clearance. Increased therapeutic and toxic effects may be expected to occur. This interaction is more likely with large or prolonged doses of salicylates.', 'DDInter', 'Small single doses of salicylates are unlikely to cause significant effects. However, patients who take large doses of salicylates or over a prolonged period of time should be closely monitored for clinical and laboratory evidence of valproate toxicity and hepatotoxicity. Free fraction of valproate may be particularly helpful in detecting this interaction. Patients should be advised to notify their physician if they experience possible symptoms of toxicity (e.g., malaise, weakness, lethargy, drowsiness, nausea, vomiting, or abdominal pain).', 'Distribution', 'Small single doses of salicylates are unlikely to cause significant effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33841/', '', 'Brivaracetam, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, More', 'Ticagrelor, Betrixaban, Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33789, 'Donepezil', 'Acetylsalicylic acid', 'Minor', 'Donepezil may increase gastric acid secretion by increasing cholinergic activity. The risk for gastrointestinal bleeding is, therefore, theoretically increased in patients receiving donepezil in combination with a nonsteroidal anti-inflammatory agent. Caution is recommended when this combination is used.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33842/', '', 'Tacrine, Aducanumab', 'Ticagrelor, Betrixaban, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33790, 'Dorzolamide (ophthalmic)', 'Acetylsalicylic acid', 'Major', 'The combination of large doses of salicylates and oral carbonic anhydrase inhibitors (CAI) may rarely result in severe metabolic acidosis and/or salicylate toxicity. The mechanism is unknown but may involve salicylate-induced displacement of CAIs from plasma protein-binding sites and reduced renal clearance, or CAI-induced plasma pH changes resulting in increased amounts of unionised salicylates entering the CNS.', 'DDInter', 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended. If coadministration is necessary, careful monitoring of the patient''s mental status and acid base balance is strongly recommended. Patients should be advised to promptly notify their physicians if they experience symptoms such as lethargy, tinnitus, confusion, nausea, vomiting, or hyperventilation.', 'Distribution', 'In general, concomitant use of antirheumatic doses of salicylates and oral CAIs is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33843/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', '', 1767369485);
