INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33819, 'Garlic', 'Dipyridamole', 'Moderate', 'Garlic may potentiate the effects of anticoagulants, platelet inhibitors and thrombolytic agents, possibly increasing the risk of bleeding. Garlic has been shown in some studies to have antithrombotic effects and may increase fibrinolysis, decrease platelet aggregation, and increase prothrombin time. Antiplatelet activity is present in garlic powder, aged garlic preparations, garlic oil, and fresh garlic (more so raw than when it is cooked). There have been isolated reports of bleeding complications associated with chronic, high dietary intake of garlic, as well as reports suggesting an interaction between warfarin and garlic resulting in increased INR.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, consumption of garlic supplements and large amounts of garlic should preferably be avoided during use of coagulation-modifying agents. In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33872/', '', 'Cilostazol, Iloprost, Treprostinil, Desirudin, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33820, 'Gatifloxacin', 'Acetylsalicylic acid', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', 'DDInter', 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33873/', '', 'Ticagrelor, Betrixaban, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, More', 'Rifamycin, Oxytetracycline, Polymyxin B, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Povidone-iodine, Neomycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33821, 'Gentamicin', 'Acetylsalicylic acid', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33874/', '', 'Rifamycin, Oxytetracycline, Polymyxin B, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Povidone-iodine, Neomycin, Polymyxin B, Rifamycin, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, More', 1767369485);
