INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (33831, 'Heparin', 'Acetylsalicylic acid', 'Moderate', 'Theoretically, the coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and heparin or low molecular weight heparin (LMWH) may potentiate the risk of bleeding. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. In addition, NSAIDs are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', 'DDInter', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving heparin or LMWH. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving heparin or LMWH.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/33884/', '', 'Selexipag, Heparin, Nebivolol, Ivabradine, Bempedoic acid, Ziconotide, Methoxyflurane, Oliceridine, Naltrexone, Oxymorphone, Tapentadol, More', 'Antithrombin III human, Protein C, Bivalirudin, Selexipag, Vorapaxar, Acetylsalicylic acid, Cangrelor, Nandrolone, Ascorbic acid, Potassium Iodide', 1767369485);
