INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34055, 'Edoxaban', 'Acetylsalicylic acid', 'Major', 'Concomitant use of edoxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding.', 'DDInter', 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Caution is recommended if edoxaban must be used with other agents that alter hemostasis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34108/', '', 'Acenocoumarol, Selexipag, Edoxaban, Amlodipine, Propranolol, Metoprolol, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, More', 'Antithrombin III human, Protein C, Bivalirudin, Selexipag, Vorapaxar, Acetylsalicylic acid, Cangrelor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34056, 'Enoxaparin', 'Acetylsalicylic acid', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', 'DDInter', 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34109/', '', 'Acenocoumarol, Selexipag, Enoxaparin, Felodipine, Amlodipine, Propranolol, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, More', 'Antithrombin III human, Protein C, Bivalirudin, Selexipag, Vorapaxar, Acetylsalicylic acid, Cangrelor', 1767369485);
