INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34069, 'Fluconazole', 'Omeprazole', 'Moderate', 'Coadministration with fluconazole may significantly increase the plasma concentrations of omeprazole and other proton pump inhibitors (PPIs). The proposed mechanism is fluconazole inhibition of PPI metabolism via CYP450 2C19 and 3A4.', 'DDInter', 'According to the manufacturers, dosage adjustment of omeprazole is not normally required when used with dual inhibitors of CYP450 2C19 and 3A4 such as fluconazole. However, it may be necessary in patients receiving higher dosages, such as those with Zollinger-Ellison syndrome. Patients should be monitored for potentially increased adverse effects of PPIs during coadministration with fluconazole. The same precaution may be applicable to other PPIs.', 'Metabolism', 'According to the manufacturers, dosage adjustment of omeprazole is not normally required when used with dual inhibitors of CYP450 2C19 and 3A4 such as fluconazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34122/', '', 'Tinidazole, Levofloxacin, Secnidazole, Flucytosine, Anidulafungin, Micafungin, Oteseconazole, Flucytosine, Chlorphenesin, Thiabendazole, Griseofulvin', 'Tinidazole, Bismuth subcitrate potassium, Vonoprazan', 1767369485);
