INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34098, 'Omeprazole', 'Letermovir', 'Moderate', 'Coadministration with letermovir may decrease the plasma concentrations of drugs that are metabolized by CYP450 2C9 and/or 2C19.', 'DDInter', 'Caution is advised when letermovir is used concomitantly with drugs that are substrates of CYP450 2C9 and/or 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when letermovir is used concomitantly with drugs that are substrates of CYP450 2C9 and/or 2C19, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34151/', '', 'Tetracycline, Levofloxacin, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Cimetidine, Nizatidine, More', 'Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Cobicistat, Brincidofovir, Remdesivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34099, 'Acetylsalicylic acid', 'Levomilnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34152/', '', 'Protein C, Acenocoumarol, Selexipag, Felodipine, Amlodipine, Propranolol, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, More', 'Phenelzine, Desipramine, Tranylcypromine, Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Tryptophan, Amoxapine, Protriptyline, Esketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34100, 'Omeprazole', 'Levothyroxine', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', 'DDInter', 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34153/', '', 'Tinidazole, Bismuth subcitrate potassium, Levofloxacin, Vonoprazan', '', 1767369485);
