INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34155, 'Theophylline', 'Omeprazole', 'Moderate', 'Proton pump inhibitors may increase the rate of theophylline absorption from sustained-release formulations. Chronic use of proton pump inhibitors produce sustained hypochlorhydria, which may enhance peristalsis in the small intestine and antiperistalsis in the proximal colon where theophylline is absorbed.', 'DDInter', 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered. Patients should be closely monitored for development of toxicity, particularly those with theophylline levels in the upper range of normal. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Absorption', 'In patients treated with proton pump inhibitors, the possibility of an enhanced pharmacologic response to sustained-release theophylline should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34208/', '', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Roflumilast, Bromotheophylline', 'Tinidazole, Levofloxacin, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Amoxicillin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34156, 'Thyroid, porcine', 'Omeprazole', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', 'DDInter', 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34209/', '', 'Tinidazole, Levofloxacin, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Cimetidine, Nizatidine, Ranitidine, Clarithromycin, More', '', 1767369485);
