INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34348, 'Boceprevir', 'Atazanavir', 'Major', 'Coadministration of boceprevir with cobicistat-boosted or ritonavir-boosted regimens of atazanavir, darunavir, or lopinavir may result in decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended. Moreover, the safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, the use of boceprevir in combination with ritonavir-boosted or cobicistat-boosted regimens of atazanavir, darunavir, or lopinavir is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34401/', '', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 1767369485);
