INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34396, 'Dabrafenib', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C8 may increase the plasma concentrations of dabrafenib and its active metabolites. Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, including many of the known inhibitors of the isoenzyme such as conivaptan, delavirdine, nefazodone, telithromycin, and most azole antifungal agents, macrolide antibiotics, and protease inhibitors.', 'DDInter', 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible. Some authorities recommend avoiding concomitant use of dabrafenib during and for 2 weeks after treatment with itraconazole. Otherwise, patients should be closely monitored for development of adverse effects such as febrile reactions (high fever or fever accompanied by rigors, hypotension, dehydration, or renal failure), hyperglycemia, uveitis, and cutaneous malignancies (e.g., squamous cell carcinoma, keratoacanthoma, melanoma).', 'Metabolism', 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34449/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34397, 'Daclatasvir', 'Atazanavir', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of daclatasvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when daclatasvir is used with moderate CYP450 3A4 inhibitors; however, according to the manufacturer, dose adjustments are not required. Patients should be monitored for adverse effects such as headache, fatigue, nausea, diarrhea, and serum lipase elevations.', 'Metabolism', 'Caution is advised when daclatasvir is used with moderate CYP450 3A4 inhibitors; however, according to the manufacturer, dose adjustments are not required.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34450/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34398, 'Danazol', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34451/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Prasterone, Raloxifene, Bazedoxifene', 1767369485);
