INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34434, 'Elagolix', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of elagolix, which is a substrate of the isoenzyme. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', 'DDInter', 'Concomitant use of elagolix 200 mg twice daily with potent CYP450 3A4 inhibitors for more than 1 month, or elagolix 150 mg once daily with potent CYP450 3A4 inhibitors for more than 6 months, is not recommended.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34487/', '', 'Lamivudine, Paritaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Nafarelin, Octreotide, Cetrorelix, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin', 1767369485);
