INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34460, 'Fedratinib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of fedratinib, which is a substrate of the isoenzyme. The risk of adverse reactions such as anemia, thrombocytopenia, diarrhea, nausea, vomiting, and liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations may increase.', 'DDInter', 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided. However, if coadministration with a potent CYP450 3A4 inhibitor is necessary, the manufacturer recommends that the dose of fedratinib be reduced to 200 mg once daily. Patients should be advised to notify their health care professional if signs and symptoms of anemia, thrombocytopenia, or gastrointestinal, hepatic, or pancreatic toxicity develop. Further dosage adjustment may be required based on patient tolerance and response. When/if concomitant therapy with a potent CYP450 3A4 inhibitor is ceased, the dose of fedratinib should first be increased to 300 mg once daily for two weeks an then increased to 400 mg once daily thereafter as tolerated.', 'Metabolism', 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34513/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
