INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34472, 'Garlic', 'Atazanavir', 'Major', 'Coadministration with garlic supplements may decrease the plasma concentrations of saquinavir and possibly other protease inhibitors (PIs). The exact mechanism is unknown but may involve induction of CYP450 3A4 metabolism and/or P-gycloprotein transport in the intestine by certain component(s) of garlic.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, garlic supplements should preferably be avoided in patients treated with PIs, particularly if they are using saquinavir as the sole PI in their antiretroviral regimen.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34525/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', '', 1767369485);
