INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34520, 'Atazanavir', 'Larotrectinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to larotrectinib may increase the risk of adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paresthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases).', 'DDInter', 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the larotrectinib dose by 50%. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34573/', '', 'Lamivudine, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
