INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34634, 'Ranitidine (bismuth citrate)', 'Atazanavir', 'Major', 'Concurrent administration of H2-receptor antagonists may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', 'DDInter', 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients. Atazanavir/cobicistat or atazanavir/ritonavir should be administered simultaneously with, and/or at least 10 hours after, the H2-receptor antagonist. If tenofovir is also prescribed, no additional precaution is necessary in treatment-naive patients, whereas the dosage of atazanavir/ritonavir should be increased to 400 mg/100 mg once a day in treatment-experienced patients. All three antiretroviral agents should be administered as a single daily dose with food. In the event that ritonavir is not tolerated by a treatment-naive patient, atazanavir may be administered at an increased dosage of 400 mg once daily at least 2 hours before and at least 10 hours after the H2-receptor antagonist.', 'Absorption', 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34687/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34635, 'Ranolazine', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ranolazine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34688/', '', 'Adenosine, Regadenoson, Alprostadil, Ubidecarenone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 1767369485);
