INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34664, 'Selumetinib', 'Atazanavir', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. When coadministered with itraconazole, a potent CYP450 3A4 inhibitor, selumetinib peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 19% and 49%, respectively.', 'DDInter', 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, a reduction in the dosage of selumetinib is recommended.', 'Metabolism', 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34717/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
