INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34667, 'Silodosin', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme. Severe hypotension and priapism may occur.', 'DDInter', 'Concomitant use of silodosin with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities recommend avoiding concomitant use of silodosin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of silodosin with potent CYP450 3A4 inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34720/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Terazosin', 1767369485);
