INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34669, 'Simvastatin', 'Atazanavir', 'Major', 'Protease inhibitors (PIs) may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors. The mechanism is PI inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma are associated with an increased risk of musculoskeletal toxicity, including myopathy and rhabdomyolysis, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 'The use of simvastatin or lovastatin is considered contraindicated in patients treated with atazanavir-ritonavir due to the potential for severe interaction. Red yeast rice (which contains lovastatin) should also be avoided in patients treated with PIs. Fluvastatin and pravastatin are probably safer alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The use of simvastatin or lovastatin is considered contraindicated in patients treated with atazanavir-ritonavir due to the potential for severe interaction.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34722/', '', 'Guar gum, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34670, 'Siponimod', 'Atazanavir', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of siponimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis.', 'DDInter', 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. Treatment with siponimod should generally not be initiated in patients who are concurrently treated with heart rate-lowering drugs. Advice from a cardiologist should be sought if coadministration of siponimod and drugs that slow heart rate or AV conduction is considered.', 'Synergism', 'Siponimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34723/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34671, 'Sirolimus', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', 'DDInter', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34724/', '', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, More', 'Cabotegravir, Maraviroc, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, Bictegravir, More', 1767369485);
