INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34673, 'Sodium bicarbonate', 'Atazanavir', 'Moderate', 'INTERVAL: Concurrent administration of antacids or other agents with acid-neutralizing effects may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus reduction in gastric acidity may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', 'DDInter', 'Atazanavir should be administered 2 hours before or 1 hour after antacids or other oral agents with acid-neutralizing effects.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/34726/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, Cobicistat, Brincidofovir, More', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin, Calcium chloride, Potassium chloride, Magnesium sulfate, Sodium acetate, More', 1767369485);
