INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34994, 'Chlorzoxazone', 'Atenolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35049/', '', 'Timolol, Felodipine, Acetylsalicylic acid, Nifedipine, Ivabradine, Timolol, Timolol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34995, 'Choline salicylate', 'Atenolol', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', 'DDInter', '-', 'Antagonism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35050/', '', 'Ivabradine', 'Ziconotide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34996, 'Cimetidine', 'Atenolol', 'Moderate', 'H2 antagonists such as cimetidine increase plasma concentrations of some oral beta-blockers. The mechanism may be related to inhibition of CYP450 metabolism of hepatically metabolized beta-blockers, and/or inhibition of renal tubular secretion of others.', 'DDInter', 'Clinical monitoring of patient response and tolerance is recommended when cimetidine is added to or discontinued from a beta-blocker regimen.', 'Metabolism, Excretion', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35051/', '', 'Nebivolol, Nebivolol, Nebivolol', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Esomeprazole, Vonoprazan, Levofloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34997, 'Cisatracurium', 'Atenolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35052/', '', 'Acetylsalicylic acid, Ivabradine', 'Botulinum toxin type A', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (34998, 'Clevidipine', 'Atenolol', 'Moderate', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers are used concomitantly with beta blockers, particularly in patients with ventricular or conduction abnormalities. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. The proposed mechanisms include additive slowing in AV conduction, reduced cardiac contractility secondary to beta-blockade, and decreased peripheral vascular resistance secondary to calcium channel blockade. In addition, some calcium channel blockers may inhibit the CYP450 metabolism of hepatically metabolized beta blockers, resulting in increased serum concentrations.', 'DDInter', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'Metabolism, Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35053/', '', 'Felodipine, Acetylsalicylic acid, Amlodipine, Nifedipine', 'Mibefradil', 1767369485);
