INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35315, 'Dronedarone', 'Chlorthalidone', 'Major', 'Dronedarone may cause dose-related prolongation of the QT interval. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of dronedarone with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated at baseline and at regular intervals throughout the duration of therapy. Any abnormalities should be corrected prior to initiating therapy, and levels should be maintained within the normal range during treatment. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Synergism', 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35371/', '', 'Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35316, 'Droperidol', 'Chlorthalidone', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', 'DDInter', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35372/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Clindamycin, Ciclopirox, Darunavir, More', 'Pimavanserin', 1767369485);
