INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35321, 'Chlorthalidone', 'Lithium carbonate', 'Major', 'Thiazide diuretics may cause a rapid increase in serum lithium levels and potentiate the risk of lithium toxicity. The exact mechanism is unknown but may be related to the sodium loss induced by thiazide diuresis, which produces a compensatory increase in proximal tubular reabsorption of sodium along with lithium.', 'DDInter', 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered. Lithium dose reductions may be required. Patients should be advised to notify their physician if they experience potential signs and symptoms of lithium toxicity such as drowsiness, dizziness, muscle weakness, vomiting, diarrhea, thirst, polyuria, tinnitus, tremor, ataxia, and blurred vision.', 'Excretion', 'Thiazide diuretics should generally not be prescribed to patients receiving lithium unless close monitoring of serum lithium levels and electrolytes can be rendered.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35377/', '', 'Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, Darunavir, Probenecid, More', '', 1767369485);
