INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35376, 'Prednisolone', 'Atezolizumab', 'Moderate', 'INTERVAL: Corticosteroids may affect the pharmacodynamic effects and efficacy of immune checkpoint inhibitors (ICI) such as programmed cell death-1 (PD-1) or programmed death ligand-1 inhibitors (PD-L1). The mechanism of this interaction is unknown; however, it has been theorized that the immunosuppressive properties of corticosteroids and the potential effect on T-cell function may decrease the efficacy of ICIs.', 'DDInter', 'The manufacturers'' product labeling should be consulted for specific recommendations. If clinically possible, the use of systemic corticosteroids should be avoided at the time of or within 30 days of starting therapy with an ICI, e.g., durvalumab, nivolumab, pembrolizumab, ipilimumab, cemiplimab, or atezolizumab. Corticosteroids may be used for the treatment of immune-mediated reactions after starting an ICI. Some manufacturers advise that corticosteroids may be used as premedication, when the ICI is used in combination with chemotherapy, as antiemetic prophylaxis, and/or to alleviate chemotherapy-related adverse effects.', 'Antagonism', 'The manufacturers'' product labeling should be consulted for specific recommendations.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35432/', '', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Fludrocortisone, Indomethacin, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Amivantamab, Rituximab, More', 1767369485);
