INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35697, 'Crizotinib', 'Atorvastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme. Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.', 'DDInter', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor. It is advisable to monitor lipid levels and use the lowest effective statin dose. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35753/', '', 'Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate, Gemfibrozil, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35698, 'Dabrafenib', 'Atorvastatin', 'Moderate', 'Coadministration with dabrafenib may alter the pharmacokinetics of HMG-CoA reductase inhibitors. Dabrafenib has been found in vitro to be an inhibitor of organic anion transporting polypeptide (OATP) 1B1 and 1B3. Since many statins including atorvastatin, fluvastatin, pitavastatin, pravastatin, rosuvastatin, and simvastatin acid (active metabolite of simvastatin) are thought to be substrates of the hepatic uptake transporters, increased plasma concentrations may be expected during coadministration with dabrafenib, although this has not been studied. On the other hand, dabrafenib has also been reported to be an inducer of CYP450 3A4 and 2C9 in vivo. Reduced plasma concentrations may occur for substrates of these isoenzymes such as atorvastatin, fluvastatin, lovastatin, and simvastatin.', 'DDInter', 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors. Clinical and laboratory monitoring are recommended whenever dabrafenib is added to or withdrawn from therapy, and the statin dosage adjusted as necessary. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Until more information is available, caution is advised when dabrafenib is prescribed with HMG-Coa reductase inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35754/', '', 'Ramipril, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate, Fenofibric acid, Mipomersen, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35699, 'Daclatasvir', 'Atorvastatin', 'Moderate', 'Coadministration with daclatasvir may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide 1B1 (OATP 1B1) or breast cancer resistance protein (BCRP) transporters, such as HMG-CoA reductase inhibitors. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by daclatasvir. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', 'DDInter', 'Caution and monitoring for adverse effects are advised during concomitant use of daclatasvir with drugs that are substrates of the OATP1B1 or BCRP transporters, particularly HMG-CoA reductase inhibitors. It is advisable to monitor lipid levels and use the lowest effective statin dose. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Excretion', 'Caution and monitoring for adverse effects are advised during concomitant use of daclatasvir with drugs that are substrates of the OATP1B1 or BCRP transporters, particularly HMG-CoA reductase inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35755/', '', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate, More', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, Pibrentasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35700, 'Dapsone', 'Atorvastatin', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35756/', '', 'Minocycline, Isotretinoin, Fluorometholone, Azelaic acid, Adapalene, Clofazimine', 'Niacin, Bempedoic acid, Indapamide, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, More', 1767369485);
