INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35720, 'Elagolix', 'Atorvastatin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of atorvastatin and its active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. Otherwise, pharmacologic response to atorvastatin should be closely monitored, and the dosage adjusted as necessary.', 'Metabolism', 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35776/', '', 'Amlodipine, Ramipril, Valsartan, Simvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, More', 'Nafarelin, Cetrorelix, Pasireotide, Ganirelix, Relugolix, Lanreotide, Gonadorelin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35721, 'Eliglustat', 'Atorvastatin', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35777/', '', 'Ramipril, Valsartan, Simvastatin, Lovastatin, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35722, 'Elotuzumab', 'Atorvastatin', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35778/', '', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35723, 'Eltrombopag', 'Atorvastatin', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of HMG-CoA reductase inhibitors (statins). The proposed mechanism is decreased clearance due to eltrombopag inhibition of the OATP 1B1-mediated hepatic uptake of statins and/or their metabolites (e.g., simvastatin acid), with fluvastatin the least likely to be significantly affected. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally.', 'DDInter', 'Lower statin dosages should be considered during coadministration with eltrombopag. In clinical trials with eltrombopag, a dosage reduction of rosuvastatin by 50% was recommended. No specific adjustment recommendations were given for the others. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Excretion', 'Lower statin dosages should be considered during coadministration with eltrombopag.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35779/', '', 'Amlodipine, Ramipril, Lisinopril, Perindopril, Acetylsalicylic acid, Niacin, Bempedoic acid, Indapamide, Fenofibrate, Gemfibrozil, Fenofibric acid, More', 'Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Lusutrombopag, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Coagulation Factor IX Human', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35724, 'Eluxadoline', 'Atorvastatin', 'Moderate', 'Coadministration with eluxadoline may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eluxadoline.', 'DDInter', 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eluxadoline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35780/', '', 'Amlodipine, Ramipril, Lisinopril, Perindopril, Acetylsalicylic acid, Niacin, Bempedoic acid, Indapamide, Fenofibrate, Fenofibric acid, Mipomersen, More', 'Opium, Diphenoxylate, Difenoxin', 1767369485);
