INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35773, 'Itraconazole', 'Atorvastatin', 'Major', 'Coadministration with itraconazole may significantly increase the plasma concentrations of atorvastatin and potentiate the risk of statin-induced myopathy. The proposed mechanism is itraconazole inhibition of atorvastatin metabolism via intestinal and hepatic CYP450 3A4.', 'DDInter', 'The benefits of using atorvastatin in combination with itraconazole should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. The lowest effective dosage of atorvastatin should be used, and should not exceed 20 mg/day when prescribed with itraconazole. Fluvastatin, pitavastatin, pravastatin, and rosuvastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of using atorvastatin in combination with itraconazole should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35829/', '', 'Indapamide, Bempedoic acid, Perindopril, Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, More', 'Flucytosine, Micafungin, Caspofungin, Anidulafungin', 1767369485);
