INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35871, 'Temsirolimus', 'Atorvastatin', 'Moderate', 'Higher dosages of lipid-lowering agents may be required during concomitant administration of sirolimus or temsirolimus. Dose-related serum lipid abnormalities, including but not limited to increased serum cholesterol and triglycerides, have been frequently reported in patients treated with sirolimus or temsirolimus.', 'DDInter', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents. Modification of the antihyperlipidemic regimen or dosages may be necessary.', 'Others', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35927/', '', 'Acetylsalicylic acid, Bempedoic acid, Indapamide, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Bempedoic acid, Alirocumab', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
