INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36396, 'Selpercatinib', 'Avanafil', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib. The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36452/', '', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide', 'Entrectinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Lapatinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36397, 'Silodosin', 'Avanafil', 'Moderate', 'Avanafil may potentiate the hypotensive effect of alpha blockers, resulting in symptomatic hypotension in some patients. Avanafil inhibits phosphodiesterase-5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation that may be additive with that induced by alpha blockers.', 'DDInter', 'Caution is advised if avanafil is used in combination with alpha blockers. Patients who demonstrate hemodynamic instability on alpha blocker therapy alone are at increased risk of symptomatic hypotension with concomitant use of avanafil. Therefore, patients should be stable on alpha blocker therapy prior to initiating avanafil, and avanafil should be initiated at the lowest recommended dosage of 50 mg. In those patients already on an optimized dosage of avanafil, alpha blocker therapy should be initiated at the lowest dosage. The safety of avanafil in combination with alpha blockers may also be affected by other variables such as intravascular volume depletion and use of other antihypertensive medications.', 'Synergism', 'Caution is advised if avanafil is used in combination with alpha blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36453/', '', 'Finasteride, Dutasteride, Solifenacin', 'Darifenacin, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36398, 'Simeprevir', 'Avanafil', 'Moderate', 'Coadministration with simeprevir may result in mild increases in plasma concentrations of phosphodiesterase-5 (PDE5) inhibitors, which are primarily metabolized by CYP450 3A4. In vivo, simeprevir mildly inhibits intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity. Therefore, a major pharmacokinetic interaction is not expected under normal circumstances.', 'DDInter', 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction. However, dosage adjustment of sildenafil or tadalafil may be needed when these agents are administered chronically for the treatment of pulmonary arterial hypertension. Consider starting with the lowest effective dose and increase as needed, with clinical monitoring as appropriate.', 'Metabolism', 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36454/', '', 'Darifenacin, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', 'Tinidazole, Ascorbic acid, Metronidazole, Rosuvastatin, Sulfadiazine, Lactic acid, Amphotericin B, Clindamycin, Ciclopirox, Probenecid, Tioconazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36399, 'Avanafil', 'Nitrous acid', 'Moderate', 'Coadministration with antihypertensive agents, diuretics, vasodilators, or phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of sodium nitrite. Since sodium nitrite can cause serious hypotension and methemoglobin formation, patients may be at increased risk for complications related to inadequate perfusion and oxygenation.', 'DDInter', 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors. Hemodynamics should be monitored during and after administration of sodium nitrite, and the infusion rate decreased if significant hypotension occurs. In addition, methemoglobin levels should be monitored and oxygen administered during treatment whenever possible.', 'Synergism', 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36455/', '', 'Apomorphine, Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Darifenacin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Promethazine, Deferoxamine, Levoleucovorin, Methionine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36400, 'Somapacitan', 'Avanafil', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of avanafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The use of avanafil in combination with CYP450 3A4 inducers is not recommended.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36456/', '', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Corticotropin, Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Tetracosactide, Mecasermin rinfabate, Pegvisomant', 1767369485);
