INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36496, 'Desirudin', 'Avapritinib', 'Major', 'Coadministration of avapritinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Patients should also seek immediate medical attention if they experience neurological signs and symptoms that may be associated with intracranial hemorrhage such as severe headache, vision problems, somnolence, or focal weakness. Brain imaging by magnetic resonance imaging (MRI) or computed tomography (CT) may be performed at the discretion of the physician based on severity and clinical presentation.', 'Synergism', 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36552/', '', 'Protein C, Acenocoumarol, Selexipag', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Larotrectinib, Bosutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36497, 'Desvenlafaxine', 'Avapritinib', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36553/', '', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, Isocarboxazid, Oxitriptan, Mirtazapine, Bupropion, Trazodone, Tryptophan, More', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Larotrectinib, Bosutinib, Vemurafenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36498, 'Dexamethasone', 'Avapritinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 'Concomitant use of avapritinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/36554/', '', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Lenvatinib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
