INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (36999, 'Norethisterone', 'Lomitapide', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 including oral contraceptives may increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme. Weak CYP450 3A4 inhibitors increase lomitapide exposure by approximately 2-fold according to the product labeling.', 'DDInter', 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives. Likewise, if oral contraceptives are initiated during treatment with lomitapide, the dosage of lomitapide may require adjustment. Patients should be advised that hormone absorption from oral contraceptives may be incomplete if vomiting or diarrhea occurs while taking lomitapide, which would warrant the use of additional contraceptive methods, especially since lomitapide may be teratogenic.', 'Metabolism', 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37055/', '', 'Nafarelin, Octreotide, Cetrorelix, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Ulipristal, Norelgestromin, Megestrol acetate, Estradiol, More', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lovastatin, Clofibrate, Cerivastatin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37000, 'Norethisterone', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', 'DDInter', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37056/', '', 'Ganirelix, Cetrorelix, Nafarelin, Gonadorelin, Dienogest, Estetrol, Etynodiol, Dienogest, Etynodiol, Dienogest, Etynodiol', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
