INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37333, 'Sirolimus', 'Azathioprine', 'Moderate', 'The use of azathioprine with other immunosuppressive or myelosuppressive agents may result in additive hematologic toxicities and increased risk of infections, particularly in transplant patients. Azathioprine alone may cause dose-related and potentially life-threatening bone marrow suppression, although it is usually reversible when managed promptly. Leucopenia, anemia, thrombocytopenia, and rarely, agranulocytosis, pancytopenia, and aplastic anemia have been reported. Dose-related reductions in numbers of circulating total white cells, granulocytes, and lymphocytes may also occur. Treatment with azathioprine alone or in combination with other immunosuppressants, particularly corticosteroids, has been associated with increased susceptibility to infections including severe or atypical infection and reactivation with varicella zoster virus, hepatitis B, cytomegalovirus, and other infectious agents.', 'DDInter', 'Concomitant use of azathioprine with other immunosuppressive or myelosuppressive agents should be avoided whenever possible. Close clinical and laboratory monitoring for hematologic toxicity is advised if coadministration is required. Since azathioprine is considered a slow-acting drug, delayed myelosuppression may occur, and effects may persist even after the drug has been discontinued. Prompt reduction in dosage or temporary withdrawal of azathioprine may be necessary if a persistently low or rapid decline in leucocyte count occurs, or if there is other evidence of bone marrow depression.', 'Synergism', 'Concomitant use of azathioprine with other immunosuppressive or myelosuppressive agents should be avoided whenever possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37389/', '', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Nandrolone, Brodalumab, Azathioprine, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, Belimumab, Emapalumab, Sutimlimab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37334, 'Smallpox (Vaccinia) Vaccine, Live', 'Azathioprine', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37390/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
